import React, { Component } from 'react'

class BindingEvent extends Component {
    constructor(props){
    super(props)
    this.state = {
        message: 'Change my state'
    }
  }

  changeState() {
    console.log(this)
    this.setState({
        message: 'State Changed!'
    },()=>{
        console.log(this)
    })
  }

    render() {
    return (
      <div>
        <div>{this.state.message}</div>
        <button onClick={this.changeState.bind(this)}>Click me!</button>
      </div>
    )
  }
}

export default BindingEvent
