import React, { Component } from 'react'

class UseSetState extends Component {

    constructor() {
        super()
        this.state = {
            count1: 0
        }
    }

    changeCount(){
        this.setState({
            count1: this.count1 + 1
        })
        console.log('Before update the count1 value ' + this.state.count1)
    }

    render() {
        return (
        <div>    
        <h1>Count - {this.state.count1}</h1>
        <button onClick={()=>{this.changeCount()}}>Increament Count1</button>
        </div>
        )
    }
}

export default UseSetState
