import React, { Component } from 'react'

class Form extends Component {
    constructor() {
        super()
        this.state = {
            username: '',
            comment: ''
        }
    }

    userHandler = (event) =>{
        this.setState({
            username: event.target.value
        })
    }

    commentHandler = (event) =>{
        this.setState({
            comment: event.target.value
        })
    }

  render() {
    return (
      <div>
        Form
        <div>Username: <input type='text' value = {this.state.username} onChange = {this.userHandler} /></div>
        <div>Comments: <textarea type='textarea' value = {this.state.comment} onChange = {this.commentHandler} /></div>
      </div>
    )
  }
}

export default Form
